mapper.select:
select wo.*,
       u1.login_name as submitterName,
       u2.login_name as handlerName,
       u3.login_name as reviewerName
from work_order wo
left join sys_user u1 on wo.submitter_id = u1.user_id
left join sys_user u2 on wo.handler_id = u2.user_id
left join sys_user u3 on wo.reviewer_id = u3.user_id


        and wo.title like concat('%', #{parm.title}, '%')


        and wo.status = #{parm.status}


        and wo.submitter_id = #{parm.submitterId}


        and wo.handler_id = #{parm.handlerId}


        and wo.reviewer_id = #{parm.reviewerId}


        and wo.company_id = #{parm.companyId}


order by wo.create_time desc
